    props.onChange(e.target.value)
  }
  return (
    <div class="wrap">
      <div class="search">
        <input
          type="text"
          class="searchTerm"
          id="input_text"
          placeholder="Search"
          onChange={(e) => handleInpurChange(e)}
          value={keyword}
        ></input>
        <button type="submit" class="searchButton">
